import { useState } from "react"

const Form = (props) => {
    const [curentSaving,setCurrentSaving] = useState();
    const [yearlyContribution,setYearlyContribution] = useState();
    const [expectedReturn,setExpectedReturn] = useState();
    const [duration,setDuration] = useState();

    const inputChangeHandler = (identifier,value) => {
        if(identifier ==='savings'){setCurrentSaving(value)}
        else if (identifier === 'contribution'){setYearlyContribution(value)}
        else if (identifier === 'return'){setExpectedReturn(value)}
        else if (identifier === 'duration'){setDuration(value)}
        }

    const submitHandler = (event) => {
        event.preventDefault();
        console.log('submit')
    }

    const resetHandler = (event) => {
        console.log('rest')
    }

    return (
        <div>
            <form className="form" onSubmit={submitHandler}>
                <div className="input-group">
                    <p>
                        <label htmlFor="current-savings">Current Savings ($)</label>
                        <input type="number" id="current-savings" onChange={(event) => inputChangeHandler('savings', event.target.value)} />
                    </p>
                    <p>
                        <label htmlFor="yearly-contribution">Yearly Savings ($)</label>
                        <input type="number" id="yearly-contribution" onChange={(event) => inputChangeHandler('contribution', event.target.value)}/>
                    </p>
                </div>
                <div className="input-group">
                    <p>
                        <label htmlFor="expected-return">
                            Expected Interest (%, per year)
                        </label>
                        <input type="number" id="expected-return" onChange={(event) => inputChangeHandler('return', event.target.value)}/>
                    </p>
                    <p>
                        <label htmlFor="duration">Investment Duration (years)</label>
                        <input type="number" id="duration" onChange={(event) => inputChangeHandler('duration', event.target.value)}/>
                    </p>
                </div>
                <p className="actions">
                    <button type="reset" className="buttonAlt" onClick={resetHandler}>
                        Reset
                    </button>
                    <button type="submit" className="button" onClick={submitHandler}>
                        Calculate
                    </button>
                </p>
            </form>
        </div>
    )
}
export default Form;
